SELECT conver.RULE_NAME,conver.DESCRIPTION 
FROM t_alarm_converge conver  
where conver.CONVERGE_ID=#{convergeId}

mapper.update:
update t_alarm_converge

	RULE_NAME = #{alarmConvergeModel.RULE_NAME},
